mapper.select:
select task_id, writer_id, writer_name, team_id, title, content, deadline, task_date
 from task_tb
  where task_id = #{taskId}

mapper.update:
update task_tb
 set
  title = #{title}, content = #{content}, deadline = #{deadline}
   where task_id = #{taskId}

mapper.select:
select file_no, file_name_key, file_name, file_path, CEIL(file_size / 1024) AS file_size, del_yn, ins_user_id, ins_date, task_id
 from task_file
  where task_id = #{taskId}

mapper.update:
update charge_tb
 set
  submit = #{submit}
   where charge_id = #{chargeId}
